//! Benchmark sample floats meant to invoke certain code paths.

use std::time::Duration;

use criterion::{black_box, criterion_group, criterion_main, Criterion};
use lexical_parse_float::FromLexical;

macro_rules! core_generator {
    ($group:ident, $name:literal, $str:ident, $t:ty) => {
        $group.bench_function($name, |bench| {
            bench.iter(|| {
                black_box($str.parse::<$t>().unwrap());
            })
        });
    };
}

macro_rules! lexical_generator {
    ($group:ident, $name:literal, $str:ident, $t:ty) => {
        $group.bench_function($name, |bench| {
            bench.iter(|| {
                black_box(<$t>::from_lexical($str.as_bytes()).unwrap());
            })
        });
    };
}

// FLOATS

// NOTE: Rust currently doesn't handle large, denormal floats
// with more than 25 significant digits. Use the 25 significant
// digits for both large and denormal.

// Example fast-path value.
const FAST_PATH: &str = "1.2345e22";
// Example disguised fast-path value.
const DISGUISED_FAST_PATH: &str = "1.2345e30";
// Example moderate path value: clearly not halfway `1 << 53`.
const MODERATE_PATH: &str = "9007199254740992.0";
// Example exactly-halfway value `(1<<53) + 1`.
const HALFWAY: &str = "9007199254740993.0";

// Example large, near-halfway values.
const LARGE10: &str = "8.98846567e307";
const LARGE20: &str = "8.988465674311580536e307";
const LARGE30: &str = "8.9884656743115805365666807213e307";
const LARGE40: &str = "8.98846567431158053656668072130502949627e307";
const LARGE50: &str = "8.988465674311580536566680721305029496276241413130e307";
const LARGE100: &str = "8.98846567431158053656668072130502949627624141313081589739713427561540454154866937524136980060240969e307";
const LARGE200: &str = "8.988465674311580536566680721305029496276241413130815897397134275615404541548669375241369800602409693534988440311420212554162910536968453110861365728770536588474293813658984423817947455605142964741514e307";
const LARGE400: &str = "8.";
const LARGE800: &str = "8.";
const LARGE1600: &str = "8.";
const LARGE3200: &str = "8.";
const LARGE6400: &str = "8.";

// Example denormal, near-halfway values.
const DENORMAL10: &str = "2.47032822e-324";
const DENORMAL20: &str = "2.470328229206232720e-324";
const DENORMAL30: &str = "2.4703282292062327208828439643e-324";
const DENORMAL40: &str = "2.47032822920623272088284396434110686182e-324";
const DENORMAL50: &str = "2.470328229206232720882843964341106861825299013071e-324";
const DENORMAL100: &str = "2.47032822920623272088284396434110686182529901307162382212792841250337753635104375932649918180817996e-324";
const DENORMAL200: &str = "2.470328229206232720882843964341106861825299013071623822127928412503377536351043759326499181808179961898982823477228588654633283551779698981993873980053909390631503565951557022639229085839244910518443e-324";
const DENORMAL400: &str = "2.-324";
const DENORMAL800: &str = "2.-324";
const DENORMAL1600: &str = "2.-324";
const DENORMAL3200: &str = "2.-324";
const DENORMAL6400: &str = "2.-324";

fn core(criterion: &mut Criterion) {
    let mut group = criterion.benchmark_group("core");
    group.measurement_time(Duration::from_secs(5));
    core_generator!(group, "fast", FAST_PATH, f64);
    core_generator!(group, "disguised", DISGUISED_FAST_PATH, f64);
    core_generator!(group, "moderate", MODERATE_PATH, f64);
    core_generator!(group, "halfway", HALFWAY, f64);
    core_generator!(group, "large10", LARGE10, f64);
    core_generator!(group, "large20", LARGE20, f64);
    core_generator!(group, "large30", LARGE30, f64);
    core_generator!(group, "large40", LARGE40, f64);
    core_generator!(group, "large50", LARGE50, f64);
    core_generator!(group, "large100", LARGE100, f64);
    core_generator!(group, "large200", LARGE200, f64);
    core_generator!(group, "large400", LARGE400, f64);
    core_generator!(group, "large800", LARGE800, f64);
    core_generator!(group, "large1600", LARGE1600, f64);
    core_generator!(group, "large3200", LARGE3200, f64);
    core_generator!(group, "large6400", LARGE6400, f64);
    core_generator!(group, "denormal10", DENORMAL10, f64);
    core_generator!(group, "denormal20", DENORMAL20, f64);
    core_generator!(group, "denormal30", DENORMAL30, f64);
    core_generator!(group, "denormal40", DENORMAL40, f64);
    core_generator!(group, "denormal50", DENORMAL50, f64);
    core_generator!(group, "denormal100", DENORMAL100, f64);
    core_generator!(group, "denormal200", DENORMAL200, f64);
    core_generator!(group, "denormal400", DENORMAL400, f64);
    core_generator!(group, "denormal800", DENORMAL800, f64);
    core_generator!(group, "denormal1600", DENORMAL1600, f64);
    core_generator!(group, "denormal3200", DENORMAL3200, f64);
    core_generator!(group, "denormal6400", DENORMAL6400, f64);
}

fn lexical(criterion: &mut Criterion) {
    let mut group = criterion.benchmark_group("lexical");
    group.measurement_time(Duration::from_secs(5));
    lexical_generator!(group, "fast", FAST_PATH, f64);
    lexical_generator!(group, "disguised", DISGUISED_FAST_PATH, f64);
    lexical_generator!(group, "moderate", MODERATE_PATH, f64);
    lexical_generator!(group, "halfway", HALFWAY, f64);
    lexical_generator!(group, "large10", LARGE10, f64);
    lexical_generator!(group, "large20", LARGE20, f64);
    lexical_generator!(group, "large30", LARGE30, f64);
    lexical_generator!(group, "large40", LARGE40, f64);
    lexical_generator!(group, "large50", LARGE50, f64);
    lexical_generator!(group, "large100", LARGE100, f64);
    lexical_generator!(group, "large200", LARGE200, f64);
    lexical_generator!(group, "large400", LARGE400, f64);
    lexical_generator!(group, "large800", LARGE800, f64);
    lexical_generator!(group, "large1600", LARGE1600, f64);
    lexical_generator!(group, "large3200", LARGE3200, f64);
    lexical_generator!(group, "large6400", LARGE6400, f64);
    lexical_generator!(group, "denormal10", DENORMAL10, f64);
    lexical_generator!(group, "denormal20", DENORMAL20, f64);
    lexical_generator!(group, "denormal30", DENORMAL30, f64);
    lexical_generator!(group, "denormal40", DENORMAL40, f64);
    lexical_generator!(group, "denormal50", DENORMAL50, f64);
    lexical_generator!(group, "denormal100", DENORMAL100, f64);
    lexical_generator!(group, "denormal200", DENORMAL200, f64);
    lexical_generator!(group, "denormal400", DENORMAL400, f64);
    lexical_generator!(group, "denormal800", DENORMAL800, f64);
    lexical_generator!(group, "denormal1600", DENORMAL1600, f64);
    lexical_generator!(group, "denormal3200", DENORMAL3200, f64);
    lexical_generator!(group, "denormal6400", DENORMAL6400, f64);
}

// MAIN

criterion_group!(core_benches, core);
criterion_group!(lexical_benches, lexical);
criterion_main!(core_benches, lexical_benches);
